SELECT CourseOfferId, CourseId, Year, Quartile,  CourseName, CourseDescription, DegreeId, ECTS, Dept, DegreeDescription, TotalECTS, TeacherId, TeacherName, Address, BirthyearTeacher, Gender FROM CourseOffers NATURAL JOIN Courses NATURAL JOIN Degrees NATURAL JOIN TeacherAssignmentsToCourses NATURAL JOIN Teachers WHERE CourseOfferId=1;
SELECT co.*, s.*, d.* FROM StudentRegistrationsToDegrees AS srd INNER JOIN Students AS s ON srd.StudentId = s.StudentId INNER JOIN StudentAssistants AS sa ON srd.StudentRegistrationId = sa.StudentRegistrationId INNER JOIN CourseOffers AS co ON sa.CourseOfferId = co.CourseOfferId INNER JOIN Degrees AS d ON srd.DegreeId = d.DegreeId WHERE srd.StudentRegistrationId = 3;
SELECT AVG(cr.Grade) FROM CourseRegistrations AS cr WHERE StudentRegistrationId = 3 GROUP BY cr.StudentRegistrationId;
